const Helper = require("../helper/data");

/*
* User  Dashboard
*/
module.exports.userDashboard = (req,res)=>{
    res.render(Helper.findView('dashboard'),{
        user: req.user,
        title: 'My Account'
    });
};



/*
* Login form
*/
module.exports.userLoginPage = (req,res)=>{

    if(req.isAuthenticated()) {
        res.redirect('/users/dashboard'); 
    }
    res.render(Helper.findView('login'), { title: 'Please Login'});
};




/*
* Register form
*/
module.exports.userRegisterPage = (req,res)=>{
     if(req.isAuthenticated()) {
        res.redirect('/users/dashboard'); 
    }
    res.render(Helper.findView('register'), { title: 'Please Register'})
};



/*
* Log out User
*/
module.exports.userLogOut = (req,res)=>{
    req.logout();
    req.flash('success_msg','Now logged out');
    res.redirect('/users/login'); 
};